import './detail.css';
import TopBar from '../../components/topbar/TopBar';
import { Container, CardColumns,Card } from 'react-bootstrap';
import emojipedia from '../../emojipedia';
import emojibook from '../../emojibook';
import gallery from '../../gallery';
import { useParams } from 'react-router';
import { Row,Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function Detail() {
  const { id }=useParams();

  return (
      <>
      <TopBar />
    <div className='detail'>
      <Container>
      <Row>
    <Col sm={8}>
      {emojibook.filter(card=>card.id==id).map((card,id) => (
      <CardColumns key={card.id} className='detailCardColumns'>
        <Card className="detailCard">
            <span className="detailImgContainer">
            <Card.Img className='detailCardImg'
             variant="top" 
             src="https://picsum.photos/200" />
             </span>
            <Card.Body>
            <Card.Title>{card.name}</Card.Title>
            <Card.Text>
                {card.meaning}
            </Card.Text>
            </Card.Body>
        </Card>
        </CardColumns>
        ))}
       </Col>
    <Col sm={4}>
      <h2>Gallery</h2>
      <ul>
        { gallery.map((gal)=>(
          <li key={gal.id}> 
          <Link to={`categories/${gal.id}`}>{gal.name}</Link> 
          </li>
        ))}
      </ul>
    </Col>
  </Row>
      </Container>
    </div>
    </>
  )
}
