import './topBar.css';
import {Container,
    Navbar,
    Row,
    Form,
    FormControl,
    Button,
} from 'react-bootstrap';
import { useState } from 'react';
import { Link } from 'react-router-dom';
import emojibook from '../../emojibook';

export default function TopBar() {
  const [searchTerm, setSearchTerm]=useState("")
  return (
    <div className='topbar'> 

    <Navbar>
        <Container>

            <Navbar.Toggle />
            <Navbar.Collapse className="justify-content-center">
            <Navbar.Brand className="navbarBrandName" href="/">EmojiBook </Navbar.Brand>
            <Form className="d-block">

            <FormControl
                type="search"
                onChange={(event)=>{setSearchTerm(event.target.value)}}
                placeholder="Search"
                className="mr-2 navbarSearch"
                aria-label="Search"
            />
            <div className="topbarSearchResult">
            {emojibook.filter((val)=>{
                              if(searchTerm==0){
                              } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())||val.emoji.toLowerCase().includes(searchTerm.toLowerCase())) {
                                return val
                              }
                            }).map((val,key)=>{
                              return (
                                 <p key={key} > 
                                     <Link className="link" to={`/detail/${val.id}`}>
                                        {val.emoji} {' '} { val.name}
                                      </Link>
                                 </p>

                                )
                              }).slice(0,4)}
            </div>
             </Form>
            </Navbar.Collapse>

        </Container>
    </Navbar>
    </div>
  )
}
