import './detail.css';
import TopBar from '../../components/topbar/TopBar';
import { Container, CardColumns,Card } from 'react-bootstrap';
import emojipedia from '../../emojipedia';
import emojibook from '../../emojibook';
import { useParams } from 'react-router';

export default function Detail() {
  const { id }=useParams();

  return (
      <>
      <TopBar />
    <div className='detail'>
      <Container>
        <div className="detailMainCon">
      {emojibook.filter(card=>card.id==id).map((card,id) => (
      <CardColumns key={card.id} className='detailCardColumns'>
        <Card className="detailCard">
            <span className="detailImgContainer">
            <Card.Img className='detailCardImg'
             variant="top" 
             src="https://picsum.photos/200" />
             </span>
            <Card.Body>
            <Card.Title>{card.name}</Card.Title>
            <Card.Text>
                {card.meaning}
            </Card.Text>
            </Card.Body>
        </Card>
        </CardColumns>
        ))}
        </div>
        <div className="detailSidebar">
          <p> Lorem ipsum dolor sit amet consectetur 
            adipisicing elit. Tempore excepturi asperiores 
            provident, fuga magni temporibus aliquid consequuntur 
            delectus a voluptates saepe illum vel, ex omnis assumenda!
             Iusto reiciendis voluptatem doloribus.</p>
        </div>
      </Container>
    </div>
    </>
  )
}
