import './home.css'
import { useState } from 'react';
import { Col,
     Container,
      Row,
      Form,
      FormControl,
      Card,
      ListGroup,
      Button, } from 'react-bootstrap';
import { Link } from 'react-router-dom'
import emojipedia from '../../emojipedia';

export default function Home() {

  const [searchTerm, setSearchTerm]=useState("");
  return (
    <div className="home">
      <Container>

          <Row className="justify-content-md-center">
                <Col xs lg="1">

                </Col>
                <Col md="auto">
                <span className="homeBrandTitle">
                 <Link to='/' className='link'>EmojiBook</Link> 
                  </span>
                            <Form className="d-flex">
                            <FormControl
                                type="search"
                                onChange={(event)=>{setSearchTerm(event.target.value)}}
                                placeholder="Search"
                                className="mr-2 homeSearch"
                                aria-label="Search"
                            />
                            </Form>
                            <div className="homeButtonGroup">
                                <Link to='/category'><Button className="homeButtonItem" variant="light">Smileys</Button></Link>
                                <Link to='/category'><Button className="homeButtonItem" variant="light">Animals</Button></Link>
                                <Link to='/category'><Button className="homeButtonItem" variant="light">Foods</Button></Link>
                                <Link to='/category'><Button className="homeButtonItem" variant="light">Symbols</Button></Link>
                            </div>
                </Col>
                <Col xs lg="1">

                </Col>
            </Row>

            {emojipedia.filter((val)=>{
                if(searchTerm==""){
                } else if (val.name.toLowerCase().includes(searchTerm.toLowerCase())||val.meaning.toLowerCase().includes(searchTerm.toLowerCase())) {
                  return val
                }
              }).map((val,key)=>{
                return (
                  <ListGroup variant="flush">
                    <ListGroup.Item key={val.id}>
                        <Row>
                            <Col md="auto"> 
                               {/* <img src="https://picsum.photos/100" alt="" />*/}
                               {val.emoji}
                            </Col>
                            <Col > 
                            <Card border="white">
                                <Card.Body>
                                  <Link to='/detail'> <Card.Title>{val.name}</Card.Title></Link>
                                    <Card.Text>
                                    {val.meaning.substring(0, 150)}
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                            </Col>
                        </Row>
                    </ListGroup.Item>
                  </ListGroup>
                  )
                })}
      </Container>
    </div>
  )
}
